import React from 'react';
import './css/Header.css';

const Header = () => {
    return (
        <header className="header">
            <h1>Escolha um Versículo da Bíblia</h1>
            <p>Encontre inspiração e reflexão em cada palavra.</p>
        </header>
    );
};

export default Header;
